          address,
          uint256,
          bytes,
          uint8,
          uint256,
          uint256,
          uint256,
          address,
          address,
          uint256
        )
      );
    return (
      to,
      GnosisSafe(payable(msg.sender)).encodeTransactionData(
        to,
        value,
        payload,
        Enum.Operation(operation),
        safeTxGas,
        baseGas,
        gasPrice,
        gasToken,
        refundReceiver,
