import "./core/Ownable.sol";

import "@openzeppelin/contracts-upgradeable/utils/structs/EnumerableSetUpgradeable.sol";

import "./core/Versionable.sol";
import "./IPrepaidCardMarket.sol";
import "./PrepaidCardManager.sol";
import "./ActionDispatcher.sol";
import "./VersionManager.sol";

contract PrepaidCardMarket is Ownable, Versionable, IPrepaidCardMarket {
  using EnumerableSetUpgradeable for EnumerableSetUpgradeable.AddressSet;

  struct SKU {
    address issuer;
    address issuingToken;
    uint256 faceValue;
    string customizationDID;
  }
  struct Reservation {
    bytes32 reservationId;
    address customer;
    uint256 blockNumber;
  }

  event Setup();
  event ItemSet(
    address prepaidCard,
    address issuer,
    address issuingToken,
    uint256 faceValue,
    string customizationDID,
    bytes32 sku
  );
  event ItemRemoved(address prepaidCard, address issuer, bytes32 sku);
  event AskSet(
    address issuer,
    address issuingToken,
    bytes32 sku,
    uint256 askPrice
  );
  event ProvisionedPrepaidCard(
    address prepaidCard,
    address customer,
    bytes32 sku,
    uint256 askPrice
  );
  event PausedToggled(bool paused);

  // keccak256 hash of the isValidSignature(bytes,bytes), with the first argument deviating from the specifications bytes32, due
  // to needing compatibility with gnosis safe which also deviates from the spec in this way
  bytes4 internal constant EIP1271_MAGIC_VALUE = 0x20c13b0b;
  bytes4 internal constant SWAP_OWNER = 0xe318b52b; //swapOwner(address,address,address)
  uint256 internal nonce;
  address public prepaidCardManagerAddress;
  address public actionDispatcher;
  address public provisioner;

  mapping(bytes32 => EnumerableSetUpgradeable.AddressSet) internal inventory; // sku => prepaid card addresses
  mapping(bytes32 => uint256) public asks; // sku => ask price (in issuing token)
  mapping(bytes32 => SKU) public skus; // sku => sku data
  mapping(address => address) public provisionedCards; // prepaid card => EOA
  mapping(bytes32 => bool) internal signatures;
  bool public paused;
  address public versionManager;

  modifier onlyHandlersOrPrepaidCardManager() {
    require(
      ActionDispatcher(actionDispatcher).isHandler(msg.sender) ||
        msg.sender == prepaidCardManagerAddress,
      "caller is not a registered action handler or PrepaidCardManager"
    );
    _;
  }
  modifier onlyHandlers() {
    require(
      ActionDispatcher(actionDispatcher).isHandler(msg.sender),
      "caller is not a registered action handler"
    );
    _;
  }
  modifier onlyProvisionerOrOwner() {
    require(
      (owner() == _msgSender()) || msg.sender == provisioner,
      "caller is not the provisioner nor the owner"
    );
    _;
  }

  function initialize(address owner) external override initializer {
    nonce = 0;
    paused = false;
    OwnableInitialize(owner);
  }

  function setup(
    address _prepaidCardManager,
    address _actionDispatcher,
    address _provisioner,
    address _versionManager
  ) external onlyOwner {
    require(_prepaidCardManager != address(0), "prepaidCardManager not set");
    require(_actionDispatcher != address(0), "actionDispatcher not set");
    require(_provisioner != address(0), "provisioner not set");
    require(_versionManager != address(0), "versionManager not set");

    prepaidCardManagerAddress = _prepaidCardManager;
    provisioner = _provisioner;
    actionDispatcher = _actionDispatcher;
    versionManager = _versionManager;

    emit Setup();
  }

  // TODO: Remove once we refactor relayer and hub to use pause() and unpause().
  // The latter is used to be compatible with OpenZeppelin's defender
  // (https://docs.openzeppelin.com/defender/admin#pauseunpause)
  function setPaused(bool _paused) external onlyOwner {
    paused = _paused;
    emit PausedToggled(_paused);
  }

  function pause() external onlyOwner {
    paused = true;
    emit PausedToggled(true);
  }

  function unpause() external onlyOwner {
    paused = false;
    emit PausedToggled(false);
  }

  function setItem(address issuer, address prepaidCard)
    external
    onlyHandlersOrPrepaidCardManager
    returns (bool)
  {
    (address issuingToken, string memory customizationDID) = validateItem(
      issuer,
      prepaidCard
    );
    PrepaidCardManager prepaidCardManager = PrepaidCardManager(
      prepaidCardManagerAddress
    );
    uint256 faceValue = prepaidCardManager.faceValue(prepaidCard);
    bytes32 sku = getSKU(issuer, issuingToken, faceValue, customizationDID);
    if (skus[sku].issuer == address(0)) {
      skus[sku].issuer = issuer;
      skus[sku].issuingToken = issuingToken;
      skus[sku].faceValue = faceValue;
      skus[sku].customizationDID = customizationDID;
    }
    inventory[sku].add(prepaidCard);
    emit ItemSet(
      prepaidCard,
      issuer,
      issuingToken,
      faceValue,
      customizationDID,
      sku
    );
    return true;
  }

  function removeItems(address issuer, address[] calldata prepaidCards)
    external
    onlyHandlers
    returns (bool)
  {
    PrepaidCardManager prepaidCardManager = PrepaidCardManager(
      prepaidCardManagerAddress
    );
    require(
      prepaidCards.length <= prepaidCardManager.MAXIMUM_NUMBER_OF_CARD(),
      "too many prepaid cards"
    );
    for (uint256 i = 0; i < prepaidCards.length; i++) {
      validateItem(issuer, prepaidCards[i]);
      bytes memory signature = contractSignature(prepaidCards[i], issuer);
      bytes32 sku = skuForPrepaidCard(prepaidCards[i]);
      inventory[sku].remove(prepaidCards[i]);
      signatures[keccak256(signature)] = true;
      // note that this is not a token transfer, so linter concerns around reetrancy
      // after transfer are not valid
      /* solhint-disable reentrancy */
      prepaidCardManager.transfer(payable(prepaidCards[i]), issuer, signature);
      signatures[keccak256(signature)] = false;
      /* solhint-enable reentrancy */
      emit ItemRemoved(prepaidCards[i], issuer, sku);
    }
    return true;
  }

  // Until we allow the purchase of prepaid cards directly from this contract, the
  // ask price will need to be enforced off-chain (in the card wallet app)
  function setAsk(
    address issuer,
    bytes32 sku,
    uint256 askPrice // a "0" askPrice removes the SKU from the market
  ) external override onlyHandlers returns (bool) {
    require(skus[sku].issuer != address(0), "Non-existent SKU");
    require(skus[sku].issuer == issuer, "SKU not owned by issuer");
    asks[sku] = askPrice;

    emit AskSet(issuer, skus[sku].issuingToken, sku, askPrice);
    return true;
  }

  function provisionPrepaidCard(address customer, bytes32 sku)
    external
    override
    onlyProvisionerOrOwner
    returns (bool)
  {
    require(!paused, "Contract is paused");
    require(inventory[sku].length() > 0, "No more prepaid cards for sku");
    require(asks[sku] > 0, "No ask price for sku");

    address prepaidCard = inventory[sku].at(0);
    provisionedCards[prepaidCard] = customer;
    bytes memory signature = contractSignature(prepaidCard, customer);
    signatures[keccak256(signature)] = true;
    inventory[sku].remove(prepaidCard);
    // note that this is not a token transfer, so linter concerns around reetrancy
    // after transfer are not valid
    /* solhint-disable reentrancy */
    PrepaidCardManager(prepaidCardManagerAddress).transfer(
      payable(prepaidCard),
      customer,
      signature
    );
    signatures[keccak256(signature)] = false;
    /* solhint-enable reentrancy */

    emit ProvisionedPrepaidCard(prepaidCard, customer, sku, asks[sku]);
    return true;
  }

  function getSKU(
    address issuer,
    address token,
    uint256 faceValue,
    string memory customizationDID
  ) public pure returns (bytes32) {
    return
      keccak256(abi.encodePacked(issuer, token, faceValue, customizationDID));
  }

  function getSkuInfo(bytes32 sku)
    external
    view
    override
    returns (
      address issuer,
      address issuingToken,
      uint256 faceValue,
      string memory customizationDID
    )
  {
    issuer = skus[sku].issuer;
    issuingToken = skus[sku].issuingToken;
    faceValue = skus[sku].faceValue;
    customizationDID = skus[sku].customizationDID;
  }

  function skuForPrepaidCard(address prepaidCard)
    public
    view
    returns (bytes32)
  {
    PrepaidCardManager prepaidCardManager = PrepaidCardManager(
      prepaidCardManagerAddress
    );
    require(
      !prepaidCardManager.hasBeenUsed(prepaidCard),
      "Can't get SKU for used prepaid card"
    );
    (
      address issuer,
      address issuingToken,
      ,
      string memory customizationDID,
      ,

    ) = prepaidCardManager.cardDetails(prepaidCard);
    uint256 faceValue = prepaidCardManager.faceValue(prepaidCard);
    return getSKU(issuer, issuingToken, faceValue, customizationDID);
  }

  function getInventory(bytes32 sku) external view returns (address[] memory) {
    return inventory[sku].values();
  }

  function getQuantity(bytes32 sku) external view returns (uint256) {
    return inventory[sku].length();
  }

  function contractSignature(address prepaidCard, address newOwner)
    internal
    returns (bytes memory)
  {
    nonce++;
    return
      abi.encodePacked(
        keccak256(abi.encodePacked(address(this), prepaidCard, newOwner, nonce))
      );
  }

  function isValidSignature(
    bytes memory, // data
    bytes memory signature
  ) external view returns (bytes4) {
    return
      signatures[keccak256(signature)] && !paused
        ? EIP1271_MAGIC_VALUE
        : bytes4(0);
  }

  function validateItem(address issuer, address prepaidCard)
    internal
    view
    returns (address issuingToken, string memory customizationDID)
  {
    PrepaidCardManager prepaidCardManager = PrepaidCardManager(
      prepaidCardManagerAddress
    );
    address expectedIssuer;
    (expectedIssuer, issuingToken, , customizationDID, , ) = prepaidCardManager
      .cardDetails(prepaidCard);

    require(
      prepaidCardManager.getPrepaidCardOwner(payable(prepaidCard)) ==
        address(this),
      "Market contract does not own the prepaid card"
    );
    require(
      expectedIssuer == issuer,
      "Specified issuer is not the issuer of the prepaid card"
    );
    require(
      !prepaidCardManager.hasBeenUsed(prepaidCard),
      "Prepaid card has been used"
    );
  }

  function cardpayVersion() external view returns (string memory) {
    return VersionManager(versionManager).version();
  }
}

pragma solidity ^0.8.9;
pragma abicoder v1;

import "@gnosis.pm/safe-contracts/contracts/GnosisSafe.sol";
import "@gnosis.pm/safe-contracts/contracts/common/Enum.sol";

import "@openzeppelin/contracts-upgradeable/token/ERC20/utils/SafeERC20Upgradeable.sol";

import "./core/Ownable.sol";

import "./token/IERC677.sol";
import "./PrepaidCardMarket.sol";
import "./TokenManager.sol";
import "./core/Safe.sol";
import "./core/Versionable.sol";
import "./SupplierManager.sol";
import "./Exchange.sol";
import "./ActionDispatcher.sol";
import "./VersionManager.sol";

contract PrepaidCardManager is Ownable, Versionable, Safe {
  using EnumerableSetUpgradeable for EnumerableSetUpgradeable.AddressSet;
  using SafeERC20Upgradeable for IERC677;

  struct CardDetail {
    address issuer;
    address issueToken;
    uint256 blockNumber;
    string customizationDID;
    bool reloadable;
    bool canPayNonMerchants;
  }
  // this is deprecated remove this when it becomes possible
  struct GasPolicy {
    bool useIssuingTokenForGas;
    bool payGasRecipient;
  }
  struct GasPolicyV2 {
    bool useGasPrice;
  }
  struct MaterializedGasPolicy {
    address gasToken;
    uint256 gasPrice;
  }
  struct ExecTransactionData {
    string action;
    address payable prepaidCard;
    address to;
    bytes data;
  }

  event Setup();
  event CreatePrepaidCard(
    address issuer,
    address owner,
    address card,
    address token,
    address createdFromDepot,
    uint256 issuingTokenAmount,
    uint256 spendAmount,
    uint256 gasFeeCollected,
    string customizationDID
  );
  event TransferredPrepaidCard(
    address prepaidCard,
    address previousOwner,
    address newOwner
  );
  event GasPolicyAdded(string action, bool useGasPrice);
  event ContractSignerRemoved(address signer);
  event TrustedCallerForCreatingPrepaidCardsWithIssuerRemoved(address caller);
  event PrepaidCardSend(
    address prepaidCard,
    uint256 spendAmount,
    uint256 rateLock,
    uint256 gasPrice,
    uint256 safeTxGas,
    uint256 baseGas,
    string action,
    bytes data,
    bytes ownerSignature
  );
  event PrepaidCardUsed(address card);

  bytes4 public constant SWAP_OWNER = 0xe318b52b; //swapOwner(address,address,address)
  bytes4 public constant TRANSFER_AND_CALL = 0x4000aea0; //transferAndCall(address,uint256,bytes)
  uint8 public constant MAXIMUM_NUMBER_OF_CARD = 15;
  uint256 public constant MINIMUM_MERCHANT_PAYMENT = 50; //in units of SPEND
  address payable public actionDispatcher;
  address payable public gasFeeReceiver;
  mapping(address => CardDetail) public cardDetails;
  uint256 public gasFeeInCARD;
  uint256 public maximumFaceValue;
  uint256 public minimumFaceValue;
  address public gasToken;
  address public exchangeAddress;
  address public tokenManager;
  address public supplierManager;
  mapping(string => GasPolicy) public gasPolicies; // this is deprecated, remove it when possible
  mapping(address => bool) public hasBeenSplit; // this is deprecated, remove it when possible
  mapping(address => bool) public hasBeenUsed;
  EnumerableSetUpgradeable.AddressSet internal contractSigners;
  mapping(string => GasPolicyV2) public gasPoliciesV2;
  address public versionManager;
  EnumerableSetUpgradeable.AddressSet
    internal trustedCallersForCreatingPrepaidCardsWithIssuer;

  modifier onlyHandlers() {
    require(
      ActionDispatcher(actionDispatcher).isHandler(msg.sender),
      "caller is not a registered action handler"
    );
    _;
  }
  modifier onlyHandlersAndContractSigners() {
    require(
      ActionDispatcher(actionDispatcher).isHandler(msg.sender) ||
        contractSigners.contains(msg.sender),
      "caller is not a registered action handler nor contract signer"
    );
    _;
  }

  /**
   * @dev Setup function sets initial storage of contract.
   * @param _tokenManager the address of the Token Manager contract
   * @param _supplierManager the address of the Supplier Manager contract
   * @param _exchangeAddress the address of the Exchange contract
   * @param _gsMasterCopy Gnosis safe Master Copy address
   * @param _gsProxyFactory Gnosis safe Proxy Factory address
   * @param _actionDispatcher Action Dispatcher address
   * @param _gasFeeReceiver The address that will receive the new prepaid card gas fee. Fee is not distributed if this is address(0)
   * @param _gasFeeInCARD the amount to charge for the gas fee for new prepaid card in units of CARD wei
   * @param _minAmount The minimum face value of a new prepaid card in units of SPEND
   * @param _maxAmount The maximum face value of a new prepaid card in units of SPEND
   */
  function setup(
    address _tokenManager,
    address _supplierManager,
    address _exchangeAddress,
    address _gsMasterCopy,
    address _gsProxyFactory,
    address payable _actionDispatcher,
    address payable _gasFeeReceiver,
    uint256 _gasFeeInCARD,
    uint256 _minAmount,
    uint256 _maxAmount,
    address[] calldata _contractSigners,
    address[] calldata _trustedCallersForCreatingPrepaidCardsWithIssuer,
    address _versionManager
  ) external onlyOwner {
    require(_tokenManager != address(0), "tokenManager not set");
    require(_supplierManager != address(0), "supplierManager not set");
    require(_exchangeAddress != address(0), "exchangeAddress not set");
    require(_gsMasterCopy != address(0), "gsMasterCopy not set");
    require(_gsProxyFactory != address(0), "gsProxyFactory not set");
    require(_actionDispatcher != address(0), "actionDispatcher not set");
    require(_versionManager != address(0), "versionManager not set");

    actionDispatcher = _actionDispatcher;
    supplierManager = _supplierManager;
    tokenManager = _tokenManager;
    exchangeAddress = _exchangeAddress;
    gasFeeReceiver = _gasFeeReceiver;
    gasFeeInCARD = _gasFeeInCARD;
    minimumFaceValue = _minAmount;
    maximumFaceValue = _maxAmount;
    versionManager = _versionManager;
    Safe.setup(_gsMasterCopy, _gsProxyFactory);
    for (uint256 i = 0; i < _contractSigners.length; i++) {
      require(
        _contractSigners[i] != address(0),
        "address in contractSigners not set"
      );

      contractSigners.add(_contractSigners[i]);
    }
    for (
      uint256 i = 0;
      i < _trustedCallersForCreatingPrepaidCardsWithIssuer.length;
      i++
    ) {
      trustedCallersForCreatingPrepaidCardsWithIssuer.add(
        _trustedCallersForCreatingPrepaidCardsWithIssuer[i]
      );
    }
    emit Setup();
  }

  /**
   * @dev Adds a new gas policy for a send action
   * @param action the send action the policy is for
   * @param useGasPrice true if we want to use the gas price as provided from the
   * relay server instead of relying on a fee to cover the gas cost
   */
  function addGasPolicy(string calldata action, bool useGasPrice)
    external
    onlyOwner
    returns (bool)
  {
    gasPoliciesV2[action].useGasPrice = useGasPrice;

    emit GasPolicyAdded(action, useGasPrice);
    return true;
  }

  function removeContractSigner(address signer) external onlyOwner {
    require(contractSigners.contains(signer), "signer not present");
    contractSigners.remove(signer);
    emit ContractSignerRemoved(signer);
  }

  function removeTrustedCallerForCreatingPrepaidCardsWithIssuer(address caller)
    external
    onlyOwner
  {
    require(
      trustedCallersForCreatingPrepaidCardsWithIssuer.contains(caller),
      "caller not present"
    );
    trustedCallersForCreatingPrepaidCardsWithIssuer.remove(caller);
    emit TrustedCallerForCreatingPrepaidCardsWithIssuerRemoved(caller);
  }

  /**
   * @dev onTokenTransfer(ERC677) - this is the ERC677 token transfer callback.
   *
   * When tokens are sent to this contract, this function will create multiple
   * prepaid cards as gnosis safes.
   *
   * See PrepaidCardManager in README for more information.
   *
   * @param from supplier or Prepaid card address
   * @param amount number of tokens sent
   * @param data encoded as (
   *  address owner (supplier's address),
   *  uint256[] issuingTokenAmounts (array of issuing token amounts to fund the creation of the prepaid cards),
   *  uint256[] spendAmounts (array of spend amounts that represent the desired face value (for reporting only)),
   *  string customizationDID (DID of prepaid card customization data),
   *  address marketAddress (prepaid card market address)
   * )
   */
  function onTokenTransfer(
    address from,
    uint256 amount,
    bytes calldata data
  ) external returns (bool) {
    require(
      TokenManager(tokenManager).isValidToken(msg.sender),
      "calling token is unaccepted"
    );

    (
      address owner,
      uint256[] memory issuingTokenAmounts,
      uint256[] memory spendAmounts,
      string memory customizationDID,
      address marketAddress,
      address issuer,
      address issuerSafe
    ) = abi.decode(
        data,
        (address, uint256[], uint256[], string, address, address, address)
      );

    require(
      owner != address(0) && issuingTokenAmounts.length > 0,
      "Prepaid card data invalid"
    );

    // The spend amounts are for reporting purposes only, there is no on-chain
    // effect from this value. Although, it might not be a bad idea that spend
    // amounts line up with the issuing token amounts--albiet we'd need to
    // introduce a rate lock mechanism if we wanted to validate this
    require(
      issuingTokenAmounts.length == spendAmounts.length,
      "the amount arrays have differing lengths"
    );

    // When issuer and issuerSafe are blank, it means the call is related to the
    // process where a prepaid card is first created, using the provided issuer as
    // the owner, and later provisioned and transfered to the customer (customer's
    // EOA is the new owner).

    if (
      (issuer == address(0) && issuerSafe == address(0)) ||
      !trustedCallersForCreatingPrepaidCardsWithIssuer.contains(from)
    ) {
      createPrepaidCards(
        owner, // issuer
        owner,
        from, // depot
        _msgSender(), // token
        amount,
        issuingTokenAmounts,
        spendAmounts,
        customizationDID,
        marketAddress
      );
    } else {
      // In case when issuer and issuerSafe are provided, it means the tokens are being
      // sent from the PrepaidCardMarketV2 contract where the prepaid cards are being
      // created and provisioned in a single step, where the issuer and owner (customer's EOA)
      // are provided during the creation of the prepaid cards.

      createPrepaidCards(
        issuer,
        owner,
        issuerSafe, // depot
        _msgSender(), // token
        amount,
        issuingTokenAmounts,
        spendAmounts,
        customizationDID,
        marketAddress
      );
    }

    return true;
  }

  function setPrepaidCardUsed(address prepaidCard)
    external
    onlyHandlers
    returns (bool)
  {
    hasBeenUsed[prepaidCard] = true;
    emit PrepaidCardUsed(prepaidCard);
    return true;
  }

  /**
   * @dev returns the face value for the prepaid card as units of SPEND.
   * @param prepaidCard the address of the prepaid card for which to get a face value
   */
  function faceValue(address prepaidCard) external view returns (uint256) {
    address issuingToken = cardDetails[prepaidCard].issueToken;
    uint256 issuingTokenBalance = IERC677(issuingToken).balanceOf(prepaidCard);
    Exchange exchange = Exchange(exchangeAddress);
    return exchange.convertToSpend(issuingToken, issuingTokenBalance);
  }

  /**
   * @dev get the price in the specified token (in units of wei) to acheive the
   * specified face value in units of SPEND. Note that the face value will drift
   * afterwards based on the exchange rate
   * @param token the issuing token for the prepaid card
   * @param spendFaceValue the desired face value for the prepaid card
   */
  function priceForFaceValue(address token, uint256 spendFaceValue)
    external
    view
    returns (uint256)
  {
    return
      Exchange(exchangeAddress).convertFromSpend(token, spendFaceValue) +
      gasFee(token) +
      100; // this is to deal with any rounding errors
  }

  /**
   * @dev get the addresses that are configured as EIP-1271 signers for prepaid cards
   */
  function getContractSigners() external view returns (address[] memory) {
    return contractSigners.values();
  }

  /**
   * @dev get the addresses that are allowed to create cards with provided issuer
   */
  function getTrustedCallersForCreatingPrepaidCardsWithIssuer()
    external
    view
    returns (address[] memory)
  {
    return trustedCallersForCreatingPrepaidCardsWithIssuer.values();
  }

  /**
   * @dev returns a boolean indicating if the prepaid card's owner is an EIP-1271 signer
   * @param prepaidCard prepaid card address
   */
  function isEIP1271Signer(address payable prepaidCard)
    public
    view
    returns (bool)
  {
    return contractSigners.contains(getPrepaidCardOwner(prepaidCard));
  }

  /**
   * @dev Pay token to merchant
   * @param prepaidCard Prepaid Card's address
   * @param spendAmount The amount of SPEND to send
   * @param rateLock the price of the issuing token in USD
   * @param gasPrice the price of the gas in terms of the gas token
   * @param safeTxGas the gas to use for the safeTx, this comes from the relay server (when gasPrice is 0 this is assumed to use nearly all the available gas)
   * @param baseGas this is the amount of gas that is independent of the specific Safe transactions, but used for general things such as signature checks and the base transaction fee. this comes from the relay server
   * @param action the name of the prepaid card action to perform, e.g. "payMerchant", "registerMerchant", "claimRevenue", etc.
   * @param data encoded data that is specific to the action being performed, e.g. the merchant safe address for the "payMerchant" action, the info DID for the "registerMerchant", etc.
   * @param ownerSignature Packed signature data ({bytes32 r}{bytes32 s}{uint8 v})
   */
  function send(
    address payable prepaidCard,
    uint256 spendAmount,
    uint256 rateLock,
    uint256 gasPrice,
    uint256 safeTxGas,
    uint256 baseGas,
    string calldata action,
    bytes calldata data,
    bytes calldata ownerSignature
  ) external returns (bool) {
    ExecTransactionData memory exTxData = validatedSendFields(
      prepaidCard,
      spendAmount,
      action,
      rateLock,
      data
    );
    emit PrepaidCardSend(
      prepaidCard,
      spendAmount,
      rateLock,
      gasPrice,
      safeTxGas,
      baseGas,
      action,
      data,
      ownerSignature
    );
    return
      execTransaction(exTxData, ownerSignature, gasPrice, safeTxGas, baseGas);
  }

  /**
   * @dev Returns the bytes that are hashed to be signed by owner.
   * @param prepaidCard The prepaid card to use for sending
   * @param spendAmount The amount of SPEND to pay the merchant
   * @param rateLock the price of the issuing token in USD
   * @param action the name of the prepaid card action to perform, e.g. "payMerchant", "registerMerchant", "claimRevenue", etc.
   * @param data encoded data that is specific to the action being performed, e.g. the merchant safe address for the "payMerchant" action, the info DID for the "registerMerchant", etc.
   */
  function getSendData(
    address payable prepaidCard,
    uint256 spendAmount,
    uint256 rateLock,
    string memory action,
    bytes memory data
  ) public view returns (bytes memory) {
    uint256 tokenAmount = Exchange(exchangeAddress).convertFromSpendWithRate(
      cardDetails[prepaidCard].issueToken,
      spendAmount,
      rateLock
    );
    return
      abi.encodeWithSelector(
        TRANSFER_AND_CALL,
        actionDispatcher,
        tokenAmount,
        abi.encode(spendAmount, rateLock, action, data)
      );
  }

  /**
   * @dev sell card for customer
   * @param prepaidCard Prepaid Card's address
   * @param newOwner the new owner of the prepaid card (the customer)
   * @param previousOwnerSignature Packed signature data ({bytes32 r}{bytes32 s}{uint8 v})
   */
  function transfer(
    address payable prepaidCard,
    address newOwner,
    bytes calldata previousOwnerSignature
  ) external onlyHandlersAndContractSigners returns (bool) {
    address previousOwner = getPrepaidCardOwner(prepaidCard);
    require(
      cardDetails[prepaidCard].issuer == previousOwner ||
        contractSigners.contains(previousOwner),
      "Has already been transferred"
    );
    require(
      !hasBeenUsed[prepaidCard],
      "Cannot transfer prepaid card that has already been used"
    );
    execTransaction(
      ExecTransactionData(
        "transfer",
        prepaidCard,
        prepaidCard,
        getTransferCardData(prepaidCard, newOwner)
      ),
      previousOwnerSignature,
      // use a 0 gas price because the outer safe tx will be paying for the gas
      0,
      0,
      0
    );
    emit TransferredPrepaidCard(prepaidCard, previousOwner, newOwner);

    return true;
  }

  /**
   * @dev Returns the bytes that are hashed to be signed by owner
   * @param prepaidCard the prepaid card address
   * @param newOwner Customer's address
   */
  function getTransferCardData(address payable prepaidCard, address newOwner)
    public
    view
    returns (bytes memory)
  {
    // Swap owner
    address oldOwner = getPrepaidCardOwner(prepaidCard);
    return
      abi.encodeWithSelector(SWAP_OWNER, address(this), oldOwner, newOwner);
  }

  /**
   * @dev check amount of card want to create.
   * convert amount to spend and check.
   */
  function isValidAmount(address token, uint256 amount)
    public
    view
    returns (bool)
  {
    uint256 amountInSPEND = Exchange(exchangeAddress).convertToSpend(
      token,
      amount - gasFee(token)
    );
    return (minimumFaceValue <= amountInSPEND &&
      amountInSPEND <= maximumFaceValue);
  }

  function gasFee(address token) public view returns (uint256) {
    if (gasFeeReceiver == address(0)) {
      return 0;
    } else {
      return Exchange(exchangeAddress).convertFromCARD(token, gasFeeInCARD);
    }
  }

  function getPrepaidCardOwner(address payable prepaidCard)
    public
    view
    returns (address)
  {
    address[] memory owners = GnosisSafe(prepaidCard).getOwners();
    require(owners.length == 2, "unexpected number of owners for prepaid card");
    return owners[0] == address(this) ? owners[1] : owners[0];
  }

  function getPrepaidCardIssuer(address prepaidCard)
    external
    view
    returns (address)
  {
    return cardDetails[prepaidCard].issuer;
  }

  function validatedSendFields(
    address prepaidCard,
    uint256 spendAmount,
    string memory action,
    uint256 rateLock,
    bytes memory data
  ) private view returns (ExecTransactionData memory) {
    require(
      cardDetails[prepaidCard].blockNumber < block.number,
      "prepaid card used too soon"
    );
    require(
      Exchange(exchangeAddress).isAllowableRate(
        cardDetails[prepaidCard].issueToken,
        rateLock
      ),
      "requested rate is beyond the allowable bounds"
    );
    return
      ExecTransactionData(
        action,
        payable(prepaidCard),
        cardDetails[prepaidCard].issueToken,
        getSendData(payable(prepaidCard), spendAmount, rateLock, action, data)
      );
  }

  /**
   * @dev Split Prepaid card
   * @param owner Supplier address
   * @param depot The Supplier's depot safe
   * @param token Token address
   * @param amountReceived Amount to split
   * @param issuingTokenAmounts array of issuing token amounts to use to fund the creation of the prepaid card
   * @param spendAmounts array of spend amounts that represent the desired face value (for reporting only)
   * @param customizationDID the customization DID for the new prepaid cards
   */
  function createPrepaidCards(
    address issuer,
    address owner,
    address depot,
    address token,
    uint256 amountReceived,
    uint256[] memory issuingTokenAmounts,
    uint256[] memory spendAmounts,
    string memory customizationDID,
    address marketAddress
  ) private returns (bool) {
    uint256 neededAmount = 0;
    uint256 numberCard = issuingTokenAmounts.length;
    require(
      numberCard <= MAXIMUM_NUMBER_OF_CARD,
      "Too many prepaid cards requested"
    );

    for (uint256 i = 0; i < numberCard; i++) {
      require(
        isValidAmount(token, issuingTokenAmounts[i]),
        "Amount below threshold"
      );
      neededAmount = neededAmount + issuingTokenAmounts[i];
    }

    require(
      amountReceived >= neededAmount,
      "Insufficient funds sent for requested amounts"
    );
    for (uint256 i = 0; i < numberCard; i++) {
      createPrepaidCard(
        issuer,
        owner,
        depot,
        token,
        issuingTokenAmounts[i],
        spendAmounts[i],
        customizationDID,
        marketAddress
      );
    }

    // refund the supplier any excess funds that they provided
    if (amountReceived > neededAmount) {
      IERC677(token).safeTransfer(depot, amountReceived - neededAmount);
    }

    return true;
  }

  /**
   * @dev Create Prepaid card
   * @param issuer issuer address
   * @param owner owner address
   * @param token token address
   * @param issuingTokenAmount amount of issuing token to use to fund the new prepaid card
   * @param spendAmount the desired face value for the new prepaid card (for reporting purposes only)
   * @param customizationDID the customization DID for the new prepaid cards
   * @return PrepaidCard address
   */
  function createPrepaidCard(
    address issuer,
    address owner,
    address depot,
    address token,
    uint256 issuingTokenAmount,
    uint256 spendAmount,
    string memory customizationDID,
    address marketAddress
  ) private returns (address) {
    address[] memory owners = new address[](2);

    owners[0] = address(this);
    owners[1] = marketAddress != address(0) ? marketAddress : owner;

    address card = createSafe(owners, 2);

    // card was created
    cardDetails[card].issuer = issuer;
    cardDetails[card].issueToken = token;
    cardDetails[card].customizationDID = customizationDID;
    cardDetails[card].blockNumber = block.number;
    cardDetails[card].reloadable = false; // future functionality
    cardDetails[card].canPayNonMerchants = false; // future functionality
    uint256 _gasFee = gasFee(token);
    if (gasFeeReceiver != address(0) && _gasFee > 0) {
      // The gasFeeReceiver is a trusted address that we control
      IERC677(token).safeTransfer(gasFeeReceiver, _gasFee);
    }
    // The card is a trusted contract (gnosis safe)
    IERC677(token).safeTransfer(card, issuingTokenAmount - _gasFee);

    emit CreatePrepaidCard(
      issuer,
      owner,
      card,
      token,
      depot,
      issuingTokenAmount - _gasFee,
      spendAmount,
      _gasFee,
      customizationDID
    );

    if (marketAddress != address(0)) {
      PrepaidCardMarket(marketAddress).setItem(owner, card);
    }

    return card;
  }

  /**
   * @dev adapter execTransaction for prepaid card(gnosis safe)
   * @param exData an ExecTransactionData struct populated with the data to be executed
   * @param signature Packed signature data ({bytes32 r}{bytes32 s}{uint8 v})
   * @param gasPrice the price for gas in terms of the gas token
   * @param safeTxGas the estimated gas for the safe tx (when the gasPrice is 0, then this is not used)
   */
  function execTransaction(
    ExecTransactionData memory exData,
    bytes memory signature,
    uint256 gasPrice,
    uint256 safeTxGas,
    uint256 baseGas
  ) private returns (bool) {
    MaterializedGasPolicy memory gasPolicy = getMaterializedGasPolicy(
      exData.action,
      exData.prepaidCard,
      gasPrice
    );
    bytes memory signatures = isEIP1271Signer(exData.prepaidCard)
      ? appendToEIP1271Signature(exData.prepaidCard, signature)
      : addOwnSignature(exData.prepaidCard, signature);
    require(
      GnosisSafe(exData.prepaidCard).execTransaction(
        exData.to,
        0,
        exData.data,
        Enum.Operation.Call,
        safeTxGas,
        baseGas,
        gasPolicy.gasPrice,
        gasPolicy.gasToken,
        payable(address(0)),
        signatures
      ),
      "safe transaction was reverted"
    );

    return true;
  }

  function getMaterializedGasPolicy(
    string memory action,
    address prepaidCard,
    uint256 gasPrice
  ) internal view returns (MaterializedGasPolicy memory) {
    return
      MaterializedGasPolicy(
        cardDetails[prepaidCard].issueToken,
        gasPoliciesV2[action].useGasPrice ? gasPrice : 0
      );
  }

  /**
   * We are using a Prevalidated Signature (v = 1) type of signature for
   * signing from this contract (as opposed to EIP-1271, v = 0).
   * https://docs.gnosis.io/safe/docs/contracts_signatures/#pre-validated-signatures
   * This particular type of signature is a "pre-approved" signature. This
   * signature is considered valid only when the sender of gnosis safe exec
   * txn is the address within the signature or a GnosisSafe.approveHash() has
   * been called from the address within the signature on the safe in
   * question. In our case, since this contract issues
   * GnosisSafe.execTransaction() (in the execTransaction() function), we can
   * take advantage of the fact that all gnosis safe txn's will be sent from
   * this contract's address.
   *
   * signature type == 1
   * s = ignored
   * r = contract address with padding to 32 bytes
   * {32-bytes r}{32-bytes s}{1-byte signature type}
   */
  function getOwnSignature()
    internal
    view
    returns (bytes memory contractSignature)
  {
    // Create signature
    contractSignature = new bytes(65);
    bytes memory encodeData = abi.encode(this, address(0));
    for (uint256 i = 1; i <= 64; i++) {
      contractSignature[64 - i] = encodeData[encodeData.length - i];
    }
    bytes1 v = 0x01;
    contractSignature[64] = v;
  }

  /**
   * @dev Append the contract's own signature to the EOA signature we received from
   * the safe owner
   * @param prepaidCard the prepaid card address
   * @param signature Owner's EOA signature
   */
  function addOwnSignature(address payable prepaidCard, bytes memory signature)
    internal
    view
    returns (bytes memory signatures)
  {
    require(signature.length == 65, "Invalid signature!");

    address owner = getPrepaidCardOwner(prepaidCard);
    bytes memory ownSignature = getOwnSignature();
    signatures = new bytes(130); // 2 x 65 bytes
    // Gnosis safe signatures must be sorted by owners' address.
    if (address(this) > owner) {
      signatures = abi.encodePacked(signature, ownSignature);
    } else {
      signatures = abi.encodePacked(ownSignature, signature);
    }
  }

  /**
   * @dev Append the contract's own signature to an EIP-1271 signature we received from
   * the safe owner
   * @param prepaidCard the prepaid card address
   * @param signature Owner's EIP-1271 signature data
   */
  function appendToEIP1271Signature(
    address payable prepaidCard,
    bytes memory signature
  ) public view returns (bytes memory signatures) {
    address owner = getPrepaidCardOwner(prepaidCard);
    bytes memory ownSignature = getOwnSignature();
    uint256 eip1271SignatureLength = signature.length;
    bytes1 v = 0x00;
    // R,S,V vector for EIP-1271 signature where
    // R = the owner address
    // S = the byte offset to find the signature data
    //     which is 2 x 65 bytes because the threshold
    //     is 2 and each RSV vector is 65 bytes
    // V = signature type, 0x00 is for EIP-1271 signatures
    bytes memory eip1271RSV = abi.encodePacked(
      abi.encode(owner),
      abi.encode(uint256(130)),
      v
    );

    // Gnosis safe signatures must be sorted by owners' address. and
    // additionally EIP-1271 signatures should conclude with 32 bytes for the
    // EIP-1271 signature length, and then finally the actual EIP-1271 signature
    // data itself
    signatures = address(this) > owner
      ? abi.encodePacked(
        eip1271RSV,
        ownSignature,
        eip1271SignatureLength,
        signature
      )
      : abi.encodePacked(
        ownSignature,
        eip1271RSV,
        eip1271SignatureLength,
        signature
      );
  }

  function cardpayVersion() external view returns (string memory) {
    return VersionManager(versionManager).version();
  }
}

