    #[test]
    fn test_node_client() {
        assert_eq!(
            NodeClient::from("Geth/v1.14.12-stable-293a300d/linux-amd64/go1.23.3".to_string()),
            NodeClient::Geth
        );
        assert_eq!(
            NodeClient::from("erigon/2.60.10/linux-amd64/go1.23.3".to_string()),
            NodeClient::Erigon
        );
        assert_eq!(
            NodeClient::from("reth/v1.1.4-15fac087/x86_64-unknown-linux-gnu".to_string()),
            NodeClient::Reth
        );
        assert_eq!(
            NodeClient::from("other_client/v1.2.3".to_string()),
            NodeClient::Unknown
        );
        assert_eq!(NodeClient::from("".to_string()), NodeClient::Unknown);
    }

